import apiRequest from "./request";
import { config } from "./request";

const loginAPI = (payload) => {
    return apiRequest({
        endpoint: `http://localhost:8585/signin`,
        method: "POST",
        data: payload,
      });
}

// Create a Post Screen
const CREATE_POST_URL = (payload) => {
    return apiRequest({
        endpoint: `http://localhost:8585/createpost`,
        method: "POST",
        data: payload
    })
}

// Home Screen
const ALL_POST_URL = (payload) => {
    return apiRequest({
        endpoint: `http://localhost:8585/allpost`,
        method: "GET",
        params: payload
    })
}

// Login Screen
const LOGIN_URL = (payload) => {
    return apiRequest({
        endpoint: `http://localhost:8585/signin`,
        method: "POST",
        data: payload
    })
}

// Profile Screen
const MY_POST_URL = (payload) => {
    return apiRequest({
        endpoint: `http://localhost:8585/mypost`,
        method: "GET",
        params: payload
    })
}
const MY_BOOKMARKS_URL = (payload) => {
    return apiRequest({
        endpoint: `http://localhost:8585/bookmarks`,
        method: "GET",
        params: payload
    })
}

// ResetPassword Screen
const RESET_PWD_URL = (payload) => {
    return apiRequest({
        endpoint: `http://localhost:8585/reset-pwd`,
        method: "POST",
        data: payload
    })
}

// SignUp Screen
const SIGNUP_URL = (payload) => {
    return apiRequest({
        endpoint: `http://localhost:8585/signup`,
        method: "POST",
        data: payload
    })
}

export { loginAPI, CREATE_POST_URL, ALL_POST_URL, LOGIN_URL, MY_POST_URL, MY_BOOKMARKS_URL, RESET_PWD_URL, SIGNUP_URL,  }
