/**
 *
 * @author Anass Ferrak aka " TheLordA " <ferrak.anass@gmail.com>
 * GitHub repo: https://github.com/TheLordA/Instagram-Clone
 *
 */

import React, { useEffect, useState, useContext } from "react";
import { Link, useParams } from "react-router-dom";
import axios from "axios";
import AuthenticationContext from "../contexts/auth/Auth.context";
import { UPDATE_FOLLOW_DATA } from "../contexts/types";
import { FOLLOW_USER } from "../service/apiCalls";

// Material-UI Components
import { makeStyles } from "@mui/styles";
import Button from "@mui/material/Button";
import Grid from "@mui/material/Grid";
import IconButton from "@mui/material/IconButton";
import CssBaseline from "@mui/material/CssBaseline";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import Icon from "@mui/material/Icon";
import Avatar from "@mui/material/Avatar";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import { GET_USER_DATA, UNFOLLOW_USER } from "../service/apiCalls";

// General Styles
const useStyles = makeStyles((theme) => ({
	root: {
		maxWidth: 935,
		margin: "auto",
		padding: "60px 20px 0",
	},
	avatar_container: { margin: "auto" },
	avatar: { width: 152, height: 152 },
	editButton: {
		marginLeft: 20,
		backgroundColor: "paleturquoise",
	},
	settings: {},
}));

function TabPanel(props) {
	const { children, value, index, ...other } = props;
	return (
		<div role="tabpanel" hidden={value !== index} {...other}>
			{value === index && <Box p={3}>{children}</Box>}
		</div>
	);
}

const UserProfilePage = () => {
	const classes = useStyles();
	const [value, setValue] = useState("Posts"); // to switch between different tabs
	const { state, dispatch } = useContext(AuthenticationContext);
	const { userid } = useParams();
	const [data, setData] = useState(null);
	// const [showFollow, setShowFollow] = useState(state ? !state.Following.includes(userid) : null);
	const [showFollow, setShowFollow] = useState([]);
	// const config = axiosConfig(localStorage.getItem("jwt"));

	useEffect(() => {
		// axios.get(`http://localhost:5000/user/${userid}`, config).then((res) => {
		// 	setData(res.data);
		// });
		GET_USER_DATA({userid}).then((res) => {
			setData(res.data);
		});
	}, []);

	const followUser = () => {
		// axios.put(`http://localhost:5000/follow`, { followId: userid }, config).then((result) => {
		// 	dispatch({
		// 		type: UPDATE_FOLLOW_DATA,
		// 		payload: { Followers: result.data.Followers, Following: result.data.Following },
		// 	});
		// 	localStorage.setItem("user", JSON.stringify(result.data));
		// 	setData((prevState) => {
		// 		return {
		// 			...prevState,
		// 			user: {
		// 				...prevState.user,
		// 				Followers: [...prevState.user.Followers, result.data._id],
		// 			},
		// 		};
		// 	});
		// 	setShowFollow(false);
		// });
		FOLLOW_USER({ followId: userid }).then((result) => {
			dispatch({
				type: UPDATE_FOLLOW_DATA,
				payload: { Followers: result.data.Followers, Following: result.data.Following },
			});
			localStorage.setItem("user", JSON.stringify(result.data));
			setData((prevState) => {
				return {
					...prevState,
					user: {
						...prevState.user,
						Followers: [...prevState.user.Followers, result.data._id],
					},
				};
			});
			setShowFollow(false);
		});
	};

	const unfollowUser = () => {
		// axios.put(`http://localhost:5000/unfollow`, { unfollowId: userid }, config).then((result) => {
		// 	dispatch({
		// 		type: UPDATE_FOLLOW_DATA,
		// 		payload: { Followers: result.data.Followers, Following: result.data.Following },
		// 	});
		// 	localStorage.setItem("user", JSON.stringify(result.data));
		// 	setData((prevState) => {
		// 		const newFollower = prevState.user.Followers.filter((item) => item !== result.data._id);
		// 		return {
		// 			...prevState,
		// 			user: {
		// 				...prevState.user,
		// 				Followers: newFollower,
		// 			},
		// 		};
		// 	});
		// 	setShowFollow(true);
		// });
		UNFOLLOW_USER({ unfollowId: userid }).then((result) => {
			dispatch({
				type: UPDATE_FOLLOW_DATA,
				payload: { Followers: result.data.Followers, Following: result.data.Following },
			});
			localStorage.setItem("user", JSON.stringify(result.data));
			setData((prevState) => {
				const newFollower = prevState.user.Followers.filter((item) => item !== result.data._id);
				return {
					...prevState,
					user: {
						...prevState.user,
						Followers: newFollower,
					},
				};
			});
			setShowFollow(true);
		});
	};

	return (
        <React.Fragment>
			<CssBaseline />
			{data ? (
				<Box component="main" className={classes.root}>
					<Box mb="44px">
						<Grid container>
							<Grid item xs={4} className={classes.avatar_container}>
								<Avatar
									className={classes.avatar}
									style={{ margin: "auto" }}
									src="https://cc-media-foxit.fichub.com/image/fox-it-mondofox/e8c0f288-781d-4d0b-98ad-fd169782b53b/scene-sottacqua-per-i-sequel-di-avatar-maxw-654.jpg"
								/>
							</Grid>
							<Grid item xs={8}>
								<Box clone mb="20px">
									<Grid container alignItems="center">
										<Typography variant="h5">
											{data.user ? data.user.Name : "Is Loading ..."}
										</Typography>
										{showFollow ? (
											<Button
												className={classes.editButton}
												variant="outlined"
												onClick={() => followUser()}
											>
												Follow
											</Button>
										) : (
											<Button
												className={classes.editButton}
												variant="outlined"
												onClick={() => unfollowUser()}
											>
												UnFollow
											</Button>
										)}

										<div className={classes.settings}>
											<IconButton component={Link} to="#" size="large">
												<Icon>settings</Icon>
											</IconButton>
										</div>
									</Grid>
								</Box>
								<Box mb="20px">
									<Grid container spacing={4}>
										<Grid item>
											<Typography variant="subtitle1">
												<b>
													{data.posts
														? data.posts.length
