import React, { useEffect, useState, useContext } from "react";
import { Link, useParams } from "react-router-dom";
import axios from "axios";
import AuthenticationContext from "../contexts/auth/Auth.context";
import { UPDATE_FOLLOW_DATA } from "../contexts/types";
import { FOLLOW_USER } from "../service/apiCalls";

// Material-UI Components
import Button from "@mui/material/Button";
import Grid from "@mui/material/Grid";
import IconButton from "@mui/material/IconButton";
import CssBaseline from "@mui/material/CssBaseline";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import Icon from "@mui/material/Icon";
import Avatar from "@mui/material/Avatar";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import { GET_USER_DATA, UNFOLLOW_USER } from "../service/apiCalls";
import { useTheme } from "@mui/system";
import Navbar from "../components/Navbar";
import { updateFollowData } from "../lib/actionReducerSlice/updateFollowDataSlice";
import { useDispatch, useSelector } from "react-redux";

function TabPanel(props) {
	const { children, value, index, ...other } = props;
	return (
		<div role="tabpanel" hidden={value !== index} {...other}>
			{value === index && <Box p={3}>{children}</Box>}
		</div>
	);
}

const UserProfilePage = () => {
	const theme = useTheme();
	const [value, setValue] = useState("Posts"); // to switch between different tabs
	// const { state, dispatch } = useContext(AuthenticationContext);
	const dispatch = useDispatch();
	const { userid } = useParams();
	const [data, setData] = useState(null);
	const user = JSON.parse(localStorage?.getItem('user'));
	const [showFollow, setShowFollow] = useState(user?.Following.includes(userid));
	const followData = useSelector((state) => state.followData);

	// const [showFollow, setShowFollow] = useState([]);
	// const config = axiosConfig(localStorage.getItem("jwt"));

	useEffect(() => {
		// axios.get(`http://localhost:5000/user/${userid}`, config).then((res) => {
		// 	setData(res.data);
		// });
		GET_USER_DATA({userid}).then((res) => {
			setData(res);
		});
	}, []);

	const followUser = () => {
		// axios.put(`http://localhost:5000/follow`, { followId: userid }, config).then((result) => {
		// 	dispatch({
		// 		type: UPDATE_FOLLOW_DATA,
		// 		payload: { Followers: result.data.Followers, Following: result.data.Following },
		// 	});
		// 	localStorage.setItem("user", JSON.stringify(result.data));
		// 	setData((prevState) => {
		// 		return {
		// 			...prevState,
		// 			user: {
		// 				...prevState.user,
		// 				Followers: [...prevState.user.Followers, result.data._id],
		// 			},
		// 		};
		// 	});
		// 	setShowFollow(false);
		// });
		FOLLOW_USER({ followId: userid }).then((result) => {
			// dispatch({
			// 	type: UPDATE_FOLLOW_DATA,
			// 	payload: { Followers: result.Followers, Following: result.Following },
			// });
			dispatch(updateFollowData({ Followers: result.Followers, Following: result.Following}));
			localStorage.setItem("user", JSON.stringify(result));
			setData((prevState) => {
				return {
					...prevState,
					user: {
						...prevState.user,
						Followers: [...prevState.user.Followers, result._id],
					},
				};
			});

			setShowFollow(true);
		});
	};

	const unfollowUser = () => {
		// axios.put(`http://localhost:5000/unfollow`, { unfollowId: userid }, config).then((result) => {
		// 	dispatch({
		// 		type: UPDATE_FOLLOW_DATA,
		// 		payload: { Followers: result.data.Followers, Following: result.data.Following },
		// 	});
		// 	localStorage.setItem("user", JSON.stringify(result.data));
		// 	setData((prevState) => {
		// 		const newFollower = prevState.user.Followers.filter((item) => item !== result.data._id);
		// 		return {
		// 			...prevState,
		// 			user: {
		// 				...prevState.user,
		// 				Followers: newFollower,
		// 			},
		// 		};
		// 	});
		// 	setShowFollow(true);
		// });
		UNFOLLOW_USER({ unfollowId: userid }).then((result) => {
			// dispatch({
			// 	type: UPDATE_FOLLOW_DATA,
			// 	payload: { Followers: result.Followers, Following: result.Following },
			// });
			dispatch(updateFollowData({ Followers: result.Followers, Following: result.Following}));
			localStorage.setItem("user", JSON.stringify(result));
			setData((prevState) => {
				const newFollower = prevState.user.Followers.filter((item) => item !== result._id);
				return {
					...prevState,
					user: {
						...prevState.user,
						Followers: newFollower,
					},
				};
			});
			setShowFollow(false);
		});
	};

	return (
        <React.Fragment>
			<Navbar />
			<CssBaseline />
			{data ? (
				<Box component="main" sx={{
					maxWidth: 935,
					margin: "auto",
					padding: "60px 20px 0",
				}}>
					<Box mb="44px">
						<Grid container>
							<Grid item xs={4} sx={{ margin: "auto" }}>
								<Avatar
									sx={{ width: 152, height: 152 }}
									style={{ margin: "auto" }}
									src="https://cc-media-foxit.fichub.com/image/fox-it-mondofox/e8c0f288-781d-4d0b-98ad-fd169782b53b/scene-sottacqua-per-i-sequel-di-avatar-maxw-654.jpg"
								/>
							</Grid>
							<Grid item xs={8}>
								<Box clone mb="20px">
									<Grid container alignItems="center">
										<Typography variant="h5">
											{data.user ? data.user.Name : "Is Loading ..."}
										</Typography>
										{showFollow ? 
										(
											<Button
												sx={{marginLeft: 20,
													backgroundColor: "paleturquoise"}}
												variant="outlined"
												onClick={() => unfollowUser()}
											>
												UnFollow
											</Button>
										) : (
											<Button
												sx={{marginLeft: 20,
													backgroundColor: "paleturquoise"}}
												variant="outlined"
												onClick={() => followUser()}
											>
												Follow
											</Button>
										)}

										<div>
											<IconButton component={Link} to="#" size="large">
												<Icon>settings</Icon>
											</IconButton>
										</div>
									</Grid>
								</Box>
								<Box mb="20px">
									<Grid container spacing={4}>
										<Grid item>
											<Typography variant="subtitle1">
												<b>
													{data.posts
														? data.posts.length
														: "IsLoading..."}
												</b>{" "}
												posts
											</Typography>
										</Grid>
										<Grid item>
											<Typography variant="subtitle1">
												<b>
													{data.user
														? data.user.Followers.length
														: "IsLoading..."}
												</b>{" "}
												followers
											</Typography>
										</Grid>
										<Grid item>
											<Typography variant="subtitle1">
												<b>
													{data.user
														? data.user.Following.length
														: "IsLoading..."}
												</b>{" "}
												following
											</Typography>
										</Grid>
